import React from 'react';
import { useState, useEffect } from "react";
import Modal from '@mui/material/Modal';
import { useParams } from "react-router-dom";
import { useForm } from "react-hook-form";

const MarkModal = ({ mark, setMark }) => {
  const [open, setOpen] = useState(false);
  const handleClose = () => setOpen(false);
  const handleOpen = () => setOpen(true);
  const { id } = useParams();
  const mark_id = id;

  const [subject, setSubject] = useState("");
  const [internal1, setinternal1] = useState("");
  const [internal2, setinternal2] = useState("");
  const [external, setexternal] = useState("");

  const { register, reset, formState: { isSubmitSuccessful } } = useForm();

  const handleSubmit = (e) => {
    e.preventDefault();
    e.target.reset();
    const Mark = { subject, internal1, internal2, external, mark_id };
    const cache = JSON.parse(localStorage.getItem("total-marks"))
    
    if (cache != null) {
      localStorage.setItem("total-marks", JSON.stringify([...cache, Mark]));
    }
    localStorage.setItem("total-marks", JSON.stringify([...cache, Mark]));
    setMark([...mark, Mark])
    handleClose()
  };

  useEffect(() => {
    if (isSubmitSuccessful) {
      reset({}); // reset all values
    }
  }, [isSubmitSuccessful, reset]);

  return (
    <div>
      <button type="button" className='addmarks' onClick={handleOpen}>Add Marks</button>
      <Modal
    onClose={handleClose}
    open={open}
    className= ""
  >
    <div>
      <div className="modal-header">
        <h1 className="modal-title">Enter Marks</h1>
      </div>
      <div className="modal-body">
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="form-group">
            <label>SUBJECT:</label>
            <input
              type="text"
              className="form-control"
              {...register("subject")} // use register function
              required
            />
          </div>
          <div className="form-group">
            <label>First internal mark(25): </label>
            <input
              type="number"
              className="form-control"
              {...register("internal1")} // use register function
              required
            />
          </div>
          <div className="form-group">
            <label>Second internal mark(25):</label>
            <input
              type="number"
              className="form-control"
              {...register("internal2")} // use register function
              required
            />
          </div>
          <div className="form-group">
            <label>External mark(50): </label>
            <input
              type="number"
              className="form-control"
              {...register("external")} // use register function
              required
            />
          </div>
          <button>Submit</button>
        </form>
      </div>
    </div>
  </Modal>
    </div>
  );
};

export default MarkModal;
